const fruits = ['apple', 'banana', 'mango', 'orange', 'grape', 'papaya', 'pineapple', 'watermelon', 'guava', 'pear', 'kiwi', 'peach', 'plum', 'pomegranate'];
const vegetables = ['tomato', 'potato', 'onion', 'carrot', 'spinach', 'cabbage', 'cauliflower', 'broccoli', 'beans', 'peas', 'brinjal', 'okra', 'cucumber', 'pumpkin'];
const indianHome = ['chapati', 'roti', 'dosa', 'idli', 'rice', 'sambar', 'rasam', 'upma', 'poha', 'pongal', 'khichdi', 'curd rice'];

// --------- Teachable Machine ----------
let model, webcam;
async function startCamera(){
 const url=document.getElementById("modelURL").value;
 model = await tmImage.load(url+"model.json", url+"metadata.json");
 webcam = new tmImage.Webcam(300,300,true);
 await webcam.setup(); await webcam.play();
 document.getElementById("camera").appendChild(webcam.canvas);
 loop();
}
async function loop(){
 webcam.update();
 const p=await model.predict(webcam.canvas);
 p.sort((a,b)=>b.probability-a.probability);
 document.getElementById("tmResult").innerText =
  p[0].className+" ("+(p[0].probability*100).toFixed(2)+"%)";
 requestAnimationFrame(loop);
}

// --------- Search + Suggestions ----------
const input=document.getElementById("foodInput");
const sug=document.getElementById("suggestions");

input.addEventListener("input",()=>{
 sug.innerHTML="";
 const v=input.value.toLowerCase();
 if(!v) return;
 Object.keys(foodData).filter(f=>f.includes(v)).slice(0,10).forEach(f=>{
  const li=document.createElement("li");
  li.textContent=f;
  li.onclick=()=>{input.value=f; analyzeFood(); sug.innerHTML="";};
  sug.appendChild(li);
 });
});

input.addEventListener("keypress",e=>{ if(e.key==="Enter") analyzeFood(); });

function analyzeFood(){
 const name=input.value.toLowerCase();
 if(!foodData[name]){ alert("Food not found"); return; }
 let category="Moderate";
 if(fruits.includes(name) || vegetables.includes(name) || indianHome.includes(name)) category="Healthy";
 else if(foodData[name].fat>10 || foodData[name].sugar>10) category="Junk";

 document.getElementById("result").innerHTML=`
 <h3>${name.toUpperCase()}  <span class="badge ${category}">${category}</span></h3>
 <p>Calories: ${foodData[name].calories} kcal</p>
 <p>Protein: ${foodData[name].protein} g</p>
 <p>Carbs: ${foodData[name].carbs} g</p>
 <p>Fat: ${foodData[name].fat} g</p>
 <p>Fiber: ${foodData[name].fiber} g</p>
 <p>Sugar: ${foodData[name].sugar} g</p>
 <h4>Advantages</h4>
 <ul><li>Provides energy</li><li>Supplies essential nutrients</li></ul>
 <h4>Disadvantages</h4>
 <ul><li>Overconsumption may cause health issues</li></ul>
 `;
}
